mapper.select:
select

from sys_user
where id = #{id}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_user
where username = #{username}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_user
where mobile_phone = #{mobilePhone}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user
where user_email = #{userEmail}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user
where id in

	#{item}



	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user



		and id = #{id}


		and username = #{username}


		and real_name = #{realName}


		and user_password = #{userPassword}


		and password_salt = #{passwordSalt}


		and user_email = #{userEmail}


		and telephone = #{telephone}


		and mobile_phone = #{mobilePhone}


		and gender_enum = #{genderEnum}


		and register_type_enum = #{registerTypeEnum}


		and register_origin_enum = #{registerOriginEnum}


		and state_enum = #{stateEnum}


		and create_user_id = #{createUserId}


		and delete_enum = #{deleteEnum}


		and create_date >= #{createDateStartDate}


		and create_date <= #{createDateEndDate}


		and update_date >= #{updateDateStartDate}


		and update_date <= #{updateDateEndDate}


order by create_date desc

mapper.update:
update sys_user set state_enum = #{stateEnum}, update_date = #{updateDate}, update_user_id = #{updateUserId}
where id in
#{item}

update sys_user set delete_enum = #{deleteEnum}, update_date = #{updateDate}, update_user_id = #{updateUserId}, delete_date = #{deleteDate}, delete_user_id = #{deleteUserId}
where id in
#{item}
